sys_transform_entry.source_script:
answer = (function transformEntry(source) {
var gr = new GlideRecord('change_request');

var queryRecords = false;
var correlation_id = source.u_correlation_id;
var number = source.u_number;
// 1. Lookup by correlation_id
if (correlation_id && !correlation_id.nil() && correlation_id != "" ){
    log.info("sourceScript : Querying by correlation id : "+correlation_id);
	queryRecords = true;
	gr.addQuery('correlation_id',correlation_id); 
} else{
	// 2. Lookup by number
	if (number && !number.nil() && number != "" ){
	log.info("sourceScript : Querying by change request number :"+number);
	queryRecords = true;
    gr.addQuery('number',number);
	}
}
	if (queryRecords){
		gr.query();
		if (gr.next()) {
			// Update existing record
			var sys_id = gr.sys_id;
			log.info("sourceScript : Record found with sys_id :"+sys_id);
			return sys_id;
		} else {
			// Insert new record
			log.info("sourceScript : Record not found. Will be creating new record.");
			return "-2";
		}
	} else {
		// insert new record
		log.info("sourceScript : Will be creating new record.");
		return "-2";
	}
})(source);
