sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var correlation_id = source.u_correlation_id;
    var number = source.u_number;
	var queryRecords = true;
	var queryByNumber = false;

	var count = new GlideAggregate('change_request');
    count.addAggregate('COUNT');

	if (correlation_id && !correlation_id.nil() && correlation_id != ""){
		log.info("onBefore script: Correlation id is not empty in source record, will be querying by correlation id");
		count.addQuery('correlation_id',correlation_id);
	} else if (number && !number.nil() && number != "") {
		log.info("onBefore script: Change request number is not empty in source record, will be querying by number ");
		queryByNumber = true;
		count.addQuery('number',number);
	} else {
		queryRecords = false;
		log.info("onBefore script: Correlation id and number both are null or empty in source record. Will be creating new record.");
	}

if (queryRecords){
	log.info("onBefore script: Executing query");
	count.query();
	var matchingRecordsCount = 0;
	if (count.next()){
			matchingRecordsCount = count.getAggregate('COUNT');
		}
	log.info("onBefore script: Number of matching records found are: "+matchingRecordsCount);

if (matchingRecordsCount == 0 && queryByNumber){
	error = true;
	error_message = "onBefore script: No record found with change request number : "+number+". Stopping the transformation";
	} else if (matchingRecordsCount > 1){
	error = true;
	error_message = "onBefore script: More than 1 matching record found. Stopping the transformation.";
	}
}

})(source, map, log, target);
